"use strict";

// nav
var navControl = document.querySelectorAll('.nav__btn, .nav__close'),
    navToggle = document.querySelectorAll('.nav__btn, .nav__list');
navControl.forEach(function (btn) {
  return btn.addEventListener('click', function () {
    navToggle.forEach(function (el) {
      return el.classList.toggle('open');
    });
  });
}); // background color change

var $userId = document.querySelectorAll('#userRed, #userGreen, #userBlue');
var root = document.querySelector(":root");
$userId.forEach(function (user) {
  user.addEventListener('click', function (e) {
    var userId = e.target.getAttribute("id");

    if (userId === "userRed") {
      root.style.setProperty('--red', '#FE043C');
    } else if (userId === "userGreen") {
      root.style.setProperty("--red", "green");
    } else if (userId === "userBlue") {
      root.style.setProperty('--red', 'blue');
    }
  });
}); // dropdown

var $dropDownBtn = document.querySelector('.nav-user__dropbtn'),
    $dropDownContent = document.querySelector('.nav-user__dropdown-content'),
    $dropDown = document.querySelector('.nav-user__dropdown');
$dropDownBtn.addEventListener('click', function (e) {
  e.preventDefault();

  if ($dropDownContent.classList.contains('active')) {
    $dropDownContent.classList.remove('active');
  } else {
    $dropDownContent.classList.add('active');
  }
});
$dropDownContent.addEventListener('click', function () {
  $dropDownContent.classList.remove('active');
}); //  location

var $location = document.querySelectorAll('.fixed, .about__catalog__adress');
$location.forEach(function (btn) {
  btn.addEventListener('click', function () {
    window.location = 'https://goo.gl/maps/wQeTHLZw736mvcSe9';
  });
}); // slider 

var $slider = document.querySelector('.slider');
var $sliderBtn = document.querySelectorAll('[data-target]');
var $sliderItem = document.querySelectorAll('.slider__item');
var activeSlide = 0;
$sliderItem.forEach(function (item, i) {
  if (item.classList.contains('active')) {
    activeSlide = i;
  }
});
$sliderBtn.forEach(function (btn) {
  btn.addEventListener('click', function (e) {
    sliderMove(e.target.getAttribute('id'));
  });
});

function sliderMove(direction) {
  if (direction === 'next') {
    if (activeSlide < $sliderItem.length - 1) {
      activeSlide++;
    } else {
      activeSlide = 0;
    }
  } else {
    if (activeSlide > 0) {
      activeSlide--;
    } else {
      activeSlide = $sliderItem.length - 1;
    }
  }

  $sliderItem.forEach(function (item) {
    item.classList.remove('active');
  });
  $sliderItem[activeSlide].classList.add('active');
}

var interval = setInterval(function () {
  return sliderMove('next');
}, 3000);
